    <!-- Scroll to top button -->
    <button id="scrollToTop" class="fixed cursor-pointer bottom-8 right-8 w-12 h-12 bg-[#ff9000] text-white rounded-full shadow-lg opacity-0 invisible transition-all duration-300 hover:bg-[#e67e00] hover:scale-110 z-50 flex items-center justify-center scroll-progress">
        <i class="fa-solid fa-arrow-up text-lg"></i>
    </button>

    <script src="https://unpkg.com/aos@next/dist/aos.js"></script>
    <script src="https://kit.fontawesome.com/0230ec2647.js" crossorigin="anonymous"></script>

    <style>
        .scroll-progress {
            background: conic-gradient(#ff9000 var(--scroll-percentage), transparent 0deg);
        }

        .scroll-progress::before {
            content: '';
            position: absolute;
            inset: 5px;
            background: #ff9000;
            border-radius: 50%;
            z-index: -1;
        }
    </style>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const scrollToTopBtn = document.getElementById('scrollToTop');
            
            // Show/hide button based on scroll position
            window.addEventListener('scroll', function() {
                const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
                const windowHeight = window.innerHeight;
                const documentHeight = document.documentElement.scrollHeight;
                const scrollPercentage = (scrollTop / (documentHeight - windowHeight)) * 100;
                
                // Show button when scrolled down 20% of the page
                if (scrollTop > windowHeight * 0.2) {
                    scrollToTopBtn.classList.remove('opacity-0', 'invisible');
                    scrollToTopBtn.classList.add('opacity-100', 'visible');
                } else {
                    scrollToTopBtn.classList.add('opacity-0', 'invisible');
                    scrollToTopBtn.classList.remove('opacity-100', 'visible');
                }
                
                // Update border progress
                scrollToTopBtn.style.setProperty('--scroll-percentage', scrollPercentage + '%');
            });
            
            // Scroll to top functionality
            scrollToTopBtn.addEventListener('click', function() {
                window.scrollTo({
                    top: 0,
                    behavior: 'smooth'
                });
            });
        });
    </script>
</body>

